INSERT INTO `cat_route` VALUES (20,'',NULL),(25,NULL,NULL),(26,'',NULL),(30,'От станции м. Теплый Стан автобусом №531 до ост. \"Новые Ватутинки\". Время в пути 15-20 мин без учета пробок','Калужское шоссе, 14 км от МКАД, поворот направо по указателю жилой квартал \"Новые Ватутинки\"'),(31,'От станции м. Теплый Стан автобусом №531 до ост. \"Новые Ватутинки\". Время в пути 15-20 мин без учета пробок','Калужское шоссе, 14 км от МКАД, поворот направо по указателю жилой квартал \"Новые Ватутинки\"'),(32,'От станции м. Юго-Западная на автобусах №590 или №890 до ост. \"Московский\". Время в пути 15-20 мин без учета пробок','7 км от МКАД по Киевскому шоссе, далее поворот на город Московский'),(33,'От станции м. Юго-Западная автобусом №844 или отстанции м. Проспект Вернадскогодо автобусами № 830, №830с до ост. \"Улица Федосьино\". Время в пути 15-20 мин без учета пробок','Следовать по Боровскому шоссе, далее повернуть на улицу Федосьино, далее прямо 1 км, разворот в обратную сторону, через 100 м - въезд на территорию строительства'),(34,'От станции м. Кантемировская автобусом №489 или от платформы Расторгуево троллейбусами № 2,№4, №1 до ост. \"Магазин \"Всё для дома\"\". Время в пути 15-20 мин без учета пробок\nОт станции м. Домодедовская автобусом №471 \"6-й микрорайон\". Время в пути 15-20 мин без учета пробок','По трассе М4 Дон до поворота на Расторгуево, далее по проспекту Ленинского комсомола до кругового движения, на кругу повернуть направо на ул. Берёзовая, едете до конца улицы, с правой стороны расположен офис продаж ЖК \"Южное Видное\".\nПо Каширскому шоссе в сторону области до поворота на Белокаменное шоссе, далее следуйте левее по проспекту Ленинского комсомола до кругового движения, на кругу повернуть налево на ул. Берёзовая, едете до конца, с правой стороны расположен офис продаж ЖК \"Южное Видное\"'),(35,'От станции м. Марьина роща (1,7 км) 20 мин. пешком.  От метро ходят: Автобусы №9ф, №19, №12. Маршрутные такси №319м. Расстояние от метро 5 остановок.  Время в пути 8 мин без учета пробок',' Съезд с внешней стороны ТТК, направо, Шереметьевская улица 1км, дальше направо, Веткина улица, дальше резкий поворот налево, дальше съезд налево, 14-й проезд Марьиной Рощи, дальше направо, 14-й проезд Марьиной Рощи, дальше прямо до 17-й проезд Марьиной Рощи Вл. 1.'),(36,'От станции м. Университет маршрутные такси №699м, №47м до ост. \"Школа искусств\" (время в пути 15 мин) или автобусы №47, №661 до ост. \"Мичуринский просп., 21\". Время в пути 15 мин без учета пробок','Двигаться по Мичуринскому проспекту в сторону области до ул. Столетова, далее поворот направо. Через 400 метров с левой стороны будет ЖК Мичуринский'),(37,'От станции м. Выхино автобус № 841 или маршрутное такси №472 . Время в пути 15-20 мин без учета пробок\nОт Казанского вокзала на электричке до ст. \"Люберцы-1\", время в пути 30 мин. От станции до микрорайона на автобусе 4 остановки.\n','Со МКАДа съехать на Косинское шоссе и двигаться в сторону области до района Некрасовка'),(38,'От станции м. Южная: автобусы №426, №435. Ехать по Симферопольскому шоссе до пересечения с д. Боброво, далее 200 м пешком под мост. Время в пути 25 мин без учета пробок\nОт станции м. Бульвар Дмитрия Донского: автобус №18 (остановка у ТЦ \"КВАДРАТ\", напротив церкви) до Бутовского полигона. Интервал 1 час. С 6.20 утра до 20.00. ( через ж/д станцию Бутово). Время в пути 17-20 мин без учета пробок\nОт Курского вокзала: пригородные эл.поезда до станции \"Бутово\", далее автобус №18 до конечно остановки \"Бутовский полигон\", далее 15 мин  пешком.','Варшавское шоссе, 5 км от МКАД. Перед городом Щербинкой развернуться обратно в сторону Москвы и повернуть направо на Березовую аллею (указатель «Бутовский полигон»). Ориентиром для поворота служит синяя автозаправка, расположенная в конце эстакады. После поворота проехать еще 2 км до деревни Боброво. '),(39,'От станции м. Выхино автобус № 841 или маршрутное такси №472. Время в пути 15-20 мин без учета пробок\nОт Казанского вокзала на электричке до ст. \"Люберцы-1\", в пути 30 мин. От станции до микрорайона на автобусе 4 остановки.','Со МКАДа съехать на Косинское шоссе и двигаться в сторону области до района Некрасовка'),(40,'От станции м.Пятницкое шоссе на автобусах №400к, №400т, на маршрутных такси №460м, №707м до ост. \"Вахнеевка\" или на маршрутном такси Митино-Брехово до остановки \"Брёхово\" . Время в пути 20-25 мин без учета пробок','Пятницкое шоссе, 17 км от МКАД, перед АЗС \"Татнефть\" повернуть направо'),(41,'Жилой комплекс Life-Сходненская расположен в пяти минутах ходьбы от станции м. Сходненская','На автомобиле лучше добираться по улице Свободы, как со стороны МКАД, так и из центра города'),(43,'От станции м. Новогиреево - маршрутное такси № 142 (20-30 мин.);\nОт станции м. Выхино - маршрутное такси№ 100 (20-35 мин.);\nОт станции м. Новокосино - маршрутное такси № 100, 142, 487, маршрут \"Э\" (15-30 мин.);\nОт Курского вокзала 25-35 мин. на электричке до платформы \"Кучино\", утром отправление каждые 15 минут.','со стороны МКАД двигаться по Носовихинскому шоссе примерно 7 км, далее поворот направо на Почтовую улицу. Дальше двигаться по Почтовой улице и повернуть направо на улицу Речная, через 400 метров с левой стороны будет жилой комплекс Южное Кучино 2'),(44,'',''),(45,'',''),(46,'',''),(47,'',''),(48,'Жилой комплекс LIFE-Митинская находится в непосредственной близости от станции м. Митино, выход из последнего вагона по ходу движения из центра','Жилой комплекс LIFE-Митинская находится непосредственно на Пятницком шоссе'),(49,'',''),(50,'От станции м. Калужская автобусами №163, №246 или маршрутным такси №246М до ост. Киноклуб \"Эльдар\". Время в пути 7-10 мин без учета пробок','Жилой комплекс находится непосредственно на Ленинском проспекте'),(51,'',''),(52,'На общественном транспорте до ЖК ART лучше добираться от станции м. Тушинская маршрутным такси №856 до ост. Горнолыжный комплекс \"Снеж.Ком\".\nТакже можно добраться на электричке с Рижского вокзала до платформы Павшино, от платформы 20 мин. пешком до жилого комплекса','К жилому комплексу ART можно подъехать как с Волоколамского, так и с Новорижского шоссе'),(53,'От станции м. Улица Подбельского на трамваях №2 или №29 до остановки \"Богородское\". ','До ЖК Соколиный Форт удобно добираться на автомобиле по Ярославскому шоссе или Большой Черкизовской улице, далее съехать на Краснобогатырскую улицу, затем на Миллионную улицу и Вы на месте!'),(54,'От ст. м \"Университет\" на автобусе 34т, на маршрутке 525м, на троллейбусе 34, 34к до остановки \"Мосфильмовская улица, 35\". Время в пути 12-15 мин без учета пробок.\nОт ст. м \"Славянский бульвар\" на автобусе 103, 58, на маршрутке 523м, 464м (кроме вск) до остановки \"Каменная плотина\". Время в пути 11-13 мин без учета пробок.\nОт ст. м \"Минская\" пешком 10-12 мин. ','Удобнее всего подъехать к комплексу через Минскую ул.'),(55,'От станции м. «Улица Скобелевская» можно дойти пешком за 12-15 минут, либо проехать 2 остановки на автобусах № 753, 202, 118 или маршрутных такси № 967, 402м.\nОт станции м. «Бульвар Дмитрия Донского» 10 минут на автобусах № 213, 262, 94, 146.\nОстановка «Старокрымская улица»','Отдельный съезд со МКАД в районе 35-го км, расстояние 3,5 км, время в пути 6 мин. Без пробок.\nИли от развязки МКАД и Варшавского шоссе через Новобутовскую улицу, расстояние 5,3 км, время в пути 9 мин. Без Пробок.'),(56,'от ст.м. «Тушинская» - 20-25 мин на маршрутном такси №120 (конечная), далее пешком 50 м вперед.\nот ст.м. «Митино» - 20-25 мин на маршрутном такси №492, остановка «Лесопаркхоз»','От МКАД двигаться примерно 10км по Волоколамскому шоссе, ЖК \"Изумрудные холмы\" будет справа от шоссе'),(57,'Комплекс расположен в непосредственной близости от станции метро Нагорная','На автомобиле удобно подъезжать по улице Ремизова с Севастопольского проспекта, по Криворжской улице с Варшавского шоссе'),(58,'От станции м. Фили 15 минут пешком или же автобусом №653, маршрутным такси №653М до остановки \"Береговой проезд\". ','На автомобиле к  ЖК \"Фили Град\" удобнее всего подъезжать с Третьего транспортного кольца по Шмитовскому проезду. Если двигаться со стороны области, то также можно проехать по Кутузовскому проспекту, а далее по Минской и Большой Филевской улицам до Берегового проезда'),(59,'Рядом с ЖК в пяти минутах ходьбы расположена станция метро \"Водный стадион\"','На автомобиле удобнее подъезжать к жилому комплексу с Ленинградского шосее по Головинскому шоссе'),(60,'Удобнее всего добираться от станции метро \"Тушинская\" на маршрутном такси №856. После открытия пешеходного моста через Москва-реку для жителей района станет доступна станция метро \"Мякинино\"','На автомобиле удобно подъезжать с Волоколамского шоссе, в район ведет отдельная эстакада.'),(62,'От станции м. Юго-Западная на маршрутном такси №309','Со стороны Москвы двигаться по Киевскому шоссе до Апрелевки, ЖК Весна расположен по левую сторону от шоссе'),(63,'',''),(64,'От станции м. Новогиреево маршрутные такси № 108, 110, 125, 193. Ехать до остановки «Вторые ворота» (или «МРЭО ГИБДД»). Время в пути около 15 мин.','Двигаться по Шоссе Энтузиастов в сторону области до первого светофора от МКАД, на этом светофоре развернуться в сторону Москвы. Доехать до указателя «МРЭО ГИБДД». На указателе повернуть направо, и далее двигаться по главной дороге вдоль строительного забора до ворот офиса продаж объекта Балашиха-Сити (одноэтажное здание).'),(65,'От станции метро «Университет» маршрутные такси №№ 699м, 47м до остановки транспорта «Школа искусств» (время в пути 12 мин) или автобусы №№ 47, 661 до остановки транспорта «Мичуринский просп., 21»','На автомобиле двигаться из центра города по Мосфильмовской улице до пересечения с улицей Столетова, с правой стороны будет ЖК \"Мосфильмовский\"'),(66,'От станций метро «Ясенево», «Теплый стан»  автобусом №894\nОт станций метро «Улица Академика Янгеля» и «Теплый стан» автобусом №420\nОт станции метро «Юго-Западная» на автобусе с пометкой «Марьино» или маршрутном такси №894\n','Двигаться по Киевскому шоссе до города Московский. Далее через город Московский и деревню Валуево в направлении поселка Марьино. Также можно добраться по Калужскому шоссе, с шоссе свернуть на Пенино и двигаться в сторону Марьино'),(67,'От станций метро «Ясенево», «Теплый стан»  автобусом №894\nОт станций метро «Улица Академика Янгеля» и «Теплый стан» автобусом №420\nОт станции метро «Юго-Западная» на автобусе с пометкой «Марьино» или маршрутном такси №894','Двигаться по Киевскому шоссе до города Московский. Далее через город Московский и деревню Валуево в направлении поселка Марьино. Также можно добраться по Калужскому шоссе, с шоссе свернуть на Пенино и двигаться в сторону Марьино'),(68,'От станции метро «Теплый Стан» можно добраться на автобусе или маршрутном такси № 398, 433 до остановки «Санаторий \"Десна\"», затем пройти около километра пешком.','Жилой комплекс находится рядом с Калужским шоссе. Заезжать нужно по указателю «санаторий Десна», далее через 300 метров нужо повернуть направо на дорогу к офису продаж'),(69,'От станции м. «Митино» и «Пятницкое шоссе» маршрутными такси №570 или 575 до остановки «ОПХ \"Отрадное\"». Остановка автобусов и маршрутных такси расположена прямо у выхода из ЖК «Отрада».','На автомобиле примерно 3 километра от МКАД по Пятницкому шоссе'),(70,'От станции метро \"Пятницкое шоссе\" автобусом №32 до остановки «Сабурово-1»','Двигаться примерно 10 км по Пятницкому шоссе от МКАД, далее повернуть налево на Сабурово. Проехать еще примерно километр до жилого комплекса \"Пятницкие кварталы\"'),(71,'От станции метро \"Пятницкое шоссе\" автобусом №32 до остановки «Сабурово-1»','Двигаться примерно 10 км по Пятницкому шоссе от МКАД, далее повернуть налево на Сабурово. Проехать еще примерно километр до жилого комплекса \"Пятницкие кварталы\"'),(72,'От станции метро \"Пятницкое шоссе\" автобусом №32 до остановки «Сабурово-1»','Двигаться примерно 10 км по Пятницкому шоссе от МКАД, далее повернуть налево на Сабурово. Проехать еще примерно километр до жилого комплекса \"Пятницкие кварталы\"'),(73,'От станции метро \"Пятницкое шоссе\" автобусом №32 до остановки «Сабурово-1»','Двигаться примерно 10 км по Пятницкому шоссе от МКАД, далее повернуть налево на Сабурово. Проехать еще примерно километр до жилого комплекса \"Пятницкие кварталы\"'),(74,'От станции метро \"Пятницкое шоссе\" автобусами или маршрутными такси №570, 575 ло остановки \"Санаторий\"','От МКАД примерно 7 км двигаться по Пятницкому шоссе, далее повернуть налево (у ТЦ \"Отрада\")'),(75,'От станции метро Сходненская на маршрутке \"Сходненская-Путилково\".\nОт станции метро Митино на автобусе №852 до остановки «Красногорская птицефабрика»\n','Жилой комплекс расположен на Путилковском шоссе, подъезжать удобней всего со МКАД, поворот на Путилково возле ТЦ Окей'),(76,'От станции метро Сходненская на маршрутке \"Сходненская-Путилково\".\nОт станции метро Митино на автобусе №852 до остановки «Красногорская птицефабрика»\n','Жилой комплекс расположен на Путилковском шоссе, подъезжать удобней всего со МКАД, поворот на Путилково возле ТЦ Окей'),(77,'От станции метро \"Речной вокзал\" на маршрутном такси №986 до остановки \"Киностар\".\nОт станции метро \"Планерная\"на маршрутном такси №982 до той же остановки.','На автомобиле к району удобно подъезжать с Новокуркинского шоссе и с Ленинградского шоссе'),(78,'От станции метро \"Юго-Западная\" и \"Проспект Вернадского\" на маршрутном такси № 374М','Из Москвы двигаться примерно 9 км от МКАД по Боровскому шоссе, жилой комплекс будет с левой стороны дороги'),(79,'На электричке от Ленинградского вокзала до станции Крюково. Далее автобусами 374,357,497,495 до остановки \"Высокое\"','Добраться можно по Пятницкому шоссе, ехать примерно 26 км, в Жилино повернуть направо на Андреевку'),(80,'Пешком от станции метро Славянский бульвар или Пионерская','Подъезжать к дому удобнее с Кутузовского проспекта, далее на улицу Герасима Курина'),(81,'От платформы \"Химки\" Октябрьской железной дороги 10 минут пешком','От МКАД следовать по Ленинградскому шоссе, в Химках повернуть направо на улицу Маяковского, пересечь по эстакаде железную дорогу. Далее правее на улицу Юннатов, ЖК будет с правой стороны'),(82,'От платформы \"Химки\" Октябрьской железной дороги 10 минут пешком','От МКАД следовать по Ленинградскому шоссе, в Химках повернуть направо на улицу Маяковского, пересечь по эстакаде железную дорогу. Далее правее на улицу Кудрявцева, ЖК будет с правой стороны'),(83,'От станции метро \"Планерная\" маршрутными такси № 268м,189м,982,980,981,946 до остановки \"Микрорайон 2 \"Б\" Химок\"','Заезд в Химки с МКАД на улицу Молодежная, жилой комплекс появится слева примерно через 1 километр движения'),(84,'От станции метро «Речной Вокзал» на автобусе № 370, №482 и на маршрутном такси № 532 до остановки «Филино»;\nОт станции метро «Планерная» на маршрутном такси № 154 до остановки «Филино»\nОт Ленинградского вокзала на пригородной электричке до платформы Планерная, далее 10 мин. пешком в сторону Новосходненского шоссе','От МКАД 4 км. по Ленинградскому шоссе, после проезда под мостом, ведущим в ТЦ «МЕГА», съезд направо, на круговом движении первый съезд направо и 1,4 км. по Новосходненскому шоссе, на перекрестке с указателем на «Центральный Госпиталь Спецстроя России» налево 50 метров до офиса продаж\nОт МКАД 4,8 км. по Новокуркинскому шоссе, после ТЦ «МЕГА» на светофоре направо до кругового движения, третий съезд с кругового движения далее 1,4 км. по Новосходненскому шоссе, на перекрестке с указателем на «Центральный Госпиталь Спецстроя России» налево 50 метров до офиса продаж'),(85,'От станции метро \"Речной вокзал\" автобусом № 400 до ТЦ \"Мега-Химки\"\nОт станции метро \"Планерная\" маршрутным такси № 982 до ТЦ \"Мега-Химки\"','На автомобиле удобно подъезжать с Ленинградского шоссе или с Новокуркинского шоссе'),(86,'От станции метро «Речной вокзал» автобусом или маршрутным такси № 344 до остановки \"Библиотечная улица\".\nОт ж/д станции «Левобережная» автобусами 5, 344, (маршрутными такси 344, 937) 5-10 минут. До ж/д станции «Левобережная» добраться можно электропоездами Ленинградского направления от Ленинградского вокзала (время в пути 25 минут)\n','Съезд с МКАД в районе 78 километра на улицу Пожарского в Химки, далее двигаться до улицы Совхозная'),(87,'от станции метро «Речной вокзал»: на автобусе или маршрутном такси № 368.\nот станции метро «Алтуфьево»: на автобусе или маршрутном такси № 545, 544.\nот станции «Сходненская»: на автобусе №472.','по Дмитровскому шоссе: от МКАД до указателя «Яхт-клуб Адмирал». После съезда с Дмитровского шоссе через 250 м повернуть налево в город Долгопрудный, потом направо на ул. Московская, далее ехать 1 км до жилого комплекса.\nпо Лихачевскому шоссе: от МКАД до конца шоссе, повернуть направо на ул. Парковая, далее по ул. Московская, после ж/д переезда через 700 м слева вы увидите ЖК «Московские Водники.'),(88,'От ст. метро Домодедовская на маршрутных такси №505 или автобусах № 505, следующих до станции «Барыбино» (время в пути: 40 мин.). На Каширском шоссе необходимо выйти на перекрёстке, на остановке «Заборье», повернуть направо по указателю «Редькино-2». Далее следовать 10 мин. пешком до перехода через М-4, справа от Вас будет строительная площадка микрорайона «Новое Домодедово».\nОт Павелецкого вокзала, станций метро Тульская, Нагатинская и Варшавская на всех электричках, следующих до города Домодедово (время в пути: 40 – 50 мин.). Далее от железнодорожного вокзала г.Домодедово на автобусах №30, 31, 32, 55, 58 можно доехать до поворота с Каширского шоссе к аэропорту, где повернуть по указателю «Редькино-2». Далее следовать 10 мин. пешком до перехода через М-4, справа от Вас будет строительная площадка микрорайона «Новое Домодедово».','Выезд из Москвы по автомагистрали «М4 Дон» (новое Каширское шоссе), продолжение Липецкой улицы, пересечение на 28 км МКАД, по указателю Ростов-на-Дону, объект находится в 23 километрах от МКАД, с правой стороны по ходу движения. Участок застройки граничит непосредственно с трассой.'),(89,'От ст. метро Домодедовская на маршрутных такси №505 или автобусах № 505, следующих до станции «Барыбино». \n От Павелецкого вокзала, станций метро Тульская, Нагатинская и Варшавская на всех электричках, следующих до города Домодедово (время в пути: 40 – 50 мин.). Далее от железнодорожного вокзала г.Домодедово на автобусах №30, 31, 32, 55, 58 можно доехать до поворота с Каширского шоссе к аэропорту, где повернуть по указателю «Редькино-2». Далее следовать 10 мин. пешком до перехода через М-4','Выезд из Москвы по автомагистрали «М4 Дон» (новое Каширское шоссе), продолжение Липецкой улицы, пересечение на 28 км МКАД, по указателю Ростов-на-Дону, объект находится в 23 километрах от МКАД, с правой стороны по ходу движения. Участок застройки граничит непосредственно с трассой.'),(90,'Автобусом № 400 до торгового центра \"Мега-Химки\", далее пешком через железную дорогу Окябрьского направления','Заезжать в раойн удобно по Ленинградскому шоссе, далее направо на улицу Репина, пересечь по эстакаде железную дорогу. Далее налево на Коммунальный проезд и следовать прямо до ЖК \"Солнечная система\"'),(91,'От станции метро «Ясенево» автобус № 165; \nОт станции метро «Бульвар Дмитрия Донского» автобус № 213 ; \nОт станции метро «Бунинская аллея» 15 минут ходьбы или 5 мин. автобусами № 288 или № 636','В районе 35 километра МКАД съезд на Южное Бутово по улице Поляны, следовать прямо примерно 7 км, жилой комплекс будет с правой стороны'),(92,'10 минут пешком от станции метро \"Бунинская аллея\"','В районе 35 километра МКАД съезд на Южное Бутово по улице Поляны, далее налево на Веневскую улицу, после повернуть направо на Чечерский проезд и ехать до конца- впереди будет ЖК \"Новое Бутово\"'),(93,'Жилой комплекс находится в одной минуте ходьбы от станции метро \"Улица Академика Янгеля\"','Жилой комплекс находится на Вашавском шоссе, заезд через улицу Академи Янгеля'),(94,'Жилой комплекс находится в двух минутах ходьбы от станции метро \"Юго-Западная\"','Жилой комплекс находится непосредственно на Проспекте Вернадского'),(96,'От станции метро \"Юго-Западная\" автобусами № 718,752,720,720к,330м или маршрутным такси № 374м до остановки \"Говорово\"','ЖК находится на Боровском шоссе рядом со МКАД. Заезд с Боровского шоссе'),(97,'От ж/д станции \"Мытищи\" автобусами № 1,11,13,19 до ул. Юбилейной. Остановка «Хозяйственный магазин. Авто -49». \nОт станции метро «Медведково»- последний вагон из центра, выход на «проезд Шокальского», далее на автобусах 197 (до остановки «Хозтовары. Авто- 49»), 199 (до остановки «ул.Мира»), 170 (до остановки«Хозтовары. Авто- 49»).','На автомобиле с МКАД съехать на Осташковское шоссе, далее правее на Волковское шоссе и далее снова направо на улицу Борисовка- с левой стороны будет ЖК \"Ярославский\"'),(98,'От станции метро \"Медведково\" автобусом № 169 или маршрутным такси № 177 до остановки \"Ядреево\"','На автомобиле с МКАД съехать на Осташковское шоссе, далее правее на Волковское шоссе и далее съезд на улицу Колпакова.'),(99,'10 минут пешком от станции \"Мытищи\" Ярославской железной дороги','Двигаться по Ярославскому шоссе в сторону области, при въезде в Мытищи повернуть на Олимпийский проспект, далее ехать на улицу Воровского и после на Рождественскую улицу'),(100,'От железнодорожной станции \"Мытищи\" Ярославской железной дороги пешком 15 минут','Заезжать в Мытищи по Ярославскому шоссе через Олимпийский проспект, далее на улицу Рождественская'),(101,'От станции метро \"Новокосино\" на автобусах № 15, 17 или маршрутных такси  № 494, 25 ,917, 598.\nОт станции метро \"Новогиреево\" на маршрутном такси  № 533.\nОт железнодорожной станции \"Реутово\" 20 минут пешком','На автомобиле съехать в районе второго километра МКАД в Реутов на улицу Победы, далее первый поворот налево на Саловый проезд. ЖК будет с левой стороны.'),(102,'От станции метро \"Юго-Западная\" на автобусах № 688, 688к или на маршрутных такси № 308м, 519м до остановки \"Большая Очаковская улица\".','ЖК расположен непосредственно на улице Лобачевскго, подъехать сюда можно как с Аминьевского шоссе, так и с Мичуринского проспекта или проспекта Вернадского'),(103,'От станции метро \"Крылатское\" маршрутными такси № 376м,357м до остановки \"ТК \"Европарк\"\"\nОт станции метро \"Молодежная\" маршрутными такси № 150,536,101,121 или автобусом № 127 до остановки \"Черепково\"','ЖК находится на пересечении МКАД и Рублевского шоссе, заезд к комплексу возможен с Рублевского шоссе'),(104,'От станции метро \"Речной вокзал\" на автобусе № 350, 400, 440 до остановки \"Дом журналистов\" ','Двигаться примерно 16 километров от МКАД по Ленинградскому шоссе, далее поворот налево в Елино (через разворот на Ленинградском шоссе)'),(105,'От сатнции метро \"Измайловская\" 10 минут пешком\nОт станций метро \"Партизанская\" и \"Первомайская\" на трамваях № 11 и 34 до остановки «Галерея \"Измайлово\"»','При движении из центра Москвы ехать по Большой Черкизовской улице, далее на Сиреневый бульвар и после первый поворот направо на Измайловский проезд, двигаться примерно 1 км, ЖК будет с левой стороны.'),(106,'От станции метро \"Преображенска площадь\" идти по Краснобогатырской улице 5 минут, ЖК будет с левой стороны','ЖК \"Наследие\" расположен на Краснобогатырской улице, подъезжать можно как с Большой Черкизовской улицы, так и с Ярославского шоссе через Ростокинский проезд'),(107,'От железнодорожной станции \"Одинцово\" Белорусского направления железной дороги автобусом или маршрутным такси № 2','Въезжать в Одинцово по Можайскому шоссе, перед эстакадой через железнодорожные пути повернуть направо на улицу Садовая, двигаться до коца улицы, далее направо на улицу Северная- там и расположен ЖК \"Одинбург\"'),(108,'От станции метро \"Юго-Западная\" маршрутное такси №374м до остановки \"Рассказовка-2\". Далее пешком через деревню Рассказовка в сторону Киевского шоссе, идти примерно 15 минут. Также от станции метро \"Юго-Западная\" можно добраться до объекта на фирменном маршрутном такси ЖК «Рассказово»','На автомобиле двигаться по Киевскому шоссе примерно 10 км от МКАД, доехав до г. Московский повернуть направо и двигать еще примерно 2км до ЖК \"Рассказово\". Также можно добраться по Боровскому шоссе- выехав из Новопеределкино на кругу нужо повернуть налево и двигаться через д. Рассказовка примерно 1 км, ЖК будет с левой стороны.'),(109,'',''),(110,'От станции метро \"Проспект Вернадского\" на автобусах 661, 246 или маршрутках 246м, 300м до остановки «ул. Удальцова»;\nОт станции метро \"Калужская\" на маршрутке 163м до остановки «ул. Удальцова»;\nОт станции метро \"Новые Черёмушки\" на автобусе или маршрутке № 616 до остановки «Ленинский проспект»','ЖК \"The MID\" расположен непосредственно на Ленинском проспекте, добраться до него удобно как из центра города, так и со стороны области по Ленинскому проспекту или проспекту Вернадского.'),(111,'От ст. м. «Выхино» маршрутка №472 до ост. «2й Городской Отдел Милиции» или следующая остановка «АЗС» (по требованию) или автобус №772 до ост. «2й Городской Отдел Милиции»;\nОт ст. м. «Новокосино» маршрутка №527 до ост. «2й Городской Отдел Милиции»;\nОт Казанского вокзала до станции «Люберцы 1», от станции «Люберцы 1», далее маршрутка № 539 до ост. 2 «2й Городской Отдел Милиции» или автобус № 726 до ост. 2 «2й Городской Отдел Милиции»;','Добраться до жилого комплекса можно двум способами:\n- Через московский район Жулебино по Лермонтовскому проспекту, далее по Октябрьскому проспекту в Люберцах, далее поворот налево на Комсомольский проспект\n- Через московский район Кожухово по Косинскому шоссе до района Некрасовка и далее по Рождественской улице до ЖК \"Люберецкий\"'),(112,'Жилой комплекс \"Донской Олимп\" расположен в шаговой доступности от станции метро \"Шаболовская\"','Подъезжать к комплексу можно как по улице Шаболовка, так и по Серпуховскому Валу. Также можно подъехать со стороны Ленинского проспекта по улице Орджоникидзе.'),(114,'Жилой комплекс \"Sky City\" расположен всего в 10 минутах ходьбы от станции метро \"Алексеевская\"','На автомобиле удобно подъезжать по улице Бочкова с Проспекта Мира, а также по Шереметьевской улице.'),(115,'От станции метро \"Пятницкое шоссе\" на автобусе №400к до остановки \"Дворец единоборств\", далее пересесть на автобус №495 и ехать до остановки \"Больница № 6\";\nОт Ленинградского вокзала на электричке до станции \"Крюково\", далее на автобусе №495 ехать до остановки \"Больница № 6\"','На автомобиле можно добраться как по Ленинградскому шоссе, так и по Пятницкому. В обоих случаях сначала нужно ехать до города Зеленоград, а далее через Андреевку до д. Голубое'),(116,'Электричкой до станции «Железнодорожная»  с Курского вокзала (интервал движения 10-30 минут, время в пути — 25-30 минут);\nОт станции метро «Новогиреево» можно доехать на маршрутном такси № 142 (20-30 минут);\nОт метро «Выхино» и «Новокосино» — на маршрутном такси № 100 (30-40 и 15 минут соответственно)','На автомобиле двигаться примерно 9 км от МКАД по Носовихинскому шоссе, далее поворот направо на улицу Главная, далее следовать до конца улицы и впереди покажется ЖК \"Ольгино Парк\"'),(117,'От Белорусского вокзала на электричке до станции \"Звенигород\". Далее на автобусах № 24к,50,22 или маршрутном такси № 22 до остановки \"Игнатьево\"','По Новорижскому или Можайскому шоссе до города Звенигород, микрорайон находится в восточной части города в пойме реки Москва'),(118,'Электричкой от Савеловского вокзала до станции \"Лобня\", далее на автобусах № 38,21,1,6,41,48 или маршрутных такси № 21,1к доехать до остановки \"Больница\" (четвертая по счету, время в пути около трех минут)','На автомобиле лучше добираться по Дмитровскому шоссе, также возможно проехать через Ленинградское.'),(119,' От станции метро «Университет» - авт. № 103, 130, 187, 260; тр. № 17,34; м/такси № 87м, 130м, 304м, 464м, 523м, 525м, 704м до остановки «Мосфильмовская ул.»;\n От станции метро «Славянский бульвар» - авт. № 58, 103; м/такси № 464м, 523м, до остановки «Мосфильмовская ул.»;\n От станции метро «Филевский парк» - авт. № 130; м/такси № 130м, до остановки «Мосфильмовская ул.»;\n От станции метро «Киевская» - тр. №17, 34; маршрутное такси № 11м, 525м до остановки «Мосфильмовская ул.»','На автомобиле к жилому комплексу удобно подъезжать: с Кутузовского проспекта по Минской улице, по улице Мосфильмовская из центра города, по Ломоносовскому проспекту со стороны проспекта Вернадского или Ленинского проспекта'),(120,'От ж/д станции \"Люберцы\" маршрутное такси №9 до остановки \"с/т Зенино\"\n','На автомобиле удобно подъезжать по Косинскому шоссе через столичный район Некрасовка, также можно добраться через город Люберцы по Октябрьскому и Комсомольскому проспектам.'),(121,'ЖК \"Redside\" находится в пешей доступности от станций метро \"Улица 1905 года\", \"Выставочная\" и \"Деловой центр\"','На автомобиле удобно подъезжать со Звенигородского шоссе по улице Сергея Макеева'),(122,'От станции метро \"Планерная\" маршрутные такси № 946,971,982,980,981,268м,189м или троллейбусы № 203,202 до остановки \"Аптека\";\nОт станции метро \"Речной вокзал\" маршрутные такси № 662м до остановки \"Аптека\"','Сеъзд со МКАД на улицу Молодежная в Химках, через 700 метров с левой стороны будет ЖК \"Планерный\"'),(123,'от ст. м. \"Новогиреево\" автобус №17 или в маршрутное такси № 533;\nот ст. м. \"Первомайская\" автобус №15;\nот ст. м. \"Щелковская\" маршрутное такси №494;\nот ст. м. \"Черкизовская\" маршрутное такси №557;\nот ст. м. \"Выхино\" автобус № 706 или маршрутное такси №598.','Заезд в микрорайон находится в районе второго километра МКАД'),(124,'5-10 минут пешком от железнодорожной станции \"Реутово\" или от станции метро \"Новокосино\"','С МКАД съехать на Носовихинское шоссе и сразу же повернуть налево на Юбилейный проспект, проехать 1,5 километра- дом будет с правой стороны.'),(125,'',''),(126,'От м. Кантемировская автобусом №489 или от платформы Расторгуево троллейбусами № 2,4, 1 до ост «Магазин \"Всё для дома\"». \nОт м. Домодедовская автобусом №471 «6-й микрорайон»\nОт ж/д станции \"Расторгуево\" троллейбусы № 1, 2','По трассе М4 Дон до поворота на Расторгуево, далее по проспекту Ленинского комсомола до кругового движения, на кругу повернуть направо на ул. Берёзовая, едете до конца улицы, ЖК будет с левой стороны.\nПо Каширскому шоссе в сторону области до поворота на Белокаменное шоссе, далее следуйте левее по проспекту Ленинского комсомола до кругового движения, на кругу повернуть налево на ул. Берёзовая, едете до конца, ЖК будет с левой стороны.'),(127,'От м. Кантемировская автобусом №489 или от платформы Расторгуево троллейбусами № 2,4, 1 до ост «Магазин \"Всё для дома\"». \nОт м. Домодедовская автобусом №471 «6-й микрорайон»\nОт ж/д станции \"Расторгуево\" троллейбусы № 1, 2','По трассе М4 Дон до поворота на Расторгуево, далее по проспекту Ленинского комсомола до кругового движения, на кругу повернуть направо на ул. Берёзовая, ЖК будет с левой стороны.\nПо Каширскому шоссе в сторону области до поворота на Белокаменное шоссе, далее следуйте левее по проспекту Ленинского комсомола до кругового движения, на кругу повернуть налево на ул. Берёзовая, ЖК будет с левой стороны.'),(128,'От ж/д станции \"Расторгуево\" троллейбусы № 1,2, 4 до остановки \"Проспект Ленинского Комсомола\";\nОт станции метро \"Домодедовская\" автобус или маршрутное такси № 471 до остановки \"Гастроном\";\nОт станции метро \"Кантемировская\" автобус или маршрутное такси № 489 до остановки \"Гастроном\".','Двигаться по трассе М4 Дон до поворота на проспект Ленинского Комсомола, далее по проспекту Ленинского комсомола, проехать 700 метров и повернуть направо, далее двигаться прямо до ЖК «Краски жизни».'),(129,'ЖК «Самоцветы» находится в пяти минутах ходьбы от станции метро \"Жулебино\"','На автомобиле удобно подъезжать как с Октябрьского проспекта,  так с Новорязанского шоссе по улице Генерала Кузнецова'),(130,'ЖК \"Белые росы\" расположен в 3-х минутах ходьбы от станции метро \"Котельники\", также за 15 минут можно дойти от станции метро \"Жулебино\"','На автомобиле заезд к жилому комплексу осуществляется с Новорязанского шоссе'),(131,'10-15 минут пешком от железнодорожной станции \"Реутово\" или от станции метро \"Новокосино\"','С МКАД съехать на Носовихинское шоссе и сразу же повернуть налево на Юбилейный проспект, проехать примерно 2 километра- дом будет с левой стороны на перекрестке с улицей Академика Челомея'),(132,'от Курского вокзала электропоездом 30 минут до платформы \"Железнодорожный\";\nот ст.м. \"Новогиреево\" – автобус, маршрутное такси № 142;\nот ст.м. \"Новокосино\"- маршрутное такси № 142;\nот ст.м. \"Выхино\" – маршрутное такси № 100.\nот ст.м. \"Щелковская\" – маршрутное такси или автобус № 338.','На автомобиле двигаться по Носовихинскому шоссе примерно 11 км, далее повернуть направо на улицу Пролетарская, ЖК \"Центр-2\" будет с правой стороны примерно через 1,5 км'),(133,'От станции метро \"Кузьминки\" маршрутным такси № 538 до остановки \"Проезд Горбачёва\", от остановки 5 минут пешком до ЖК','На автомобиле со стороны МКАД двигаться по Новорязанскому шоссе, далее поворот направо на Лыткаринское шоссе'),(134,'От станции метро \"Кузьминки\" маршрутным такси № 538 до остановки \"Проезд Горбачёва\", от остановки 5 минут пешком до ЖК','На автомобиле со стороны МКАД двигаться по Новорязанскому шоссе, далее поворот направо на Лыткаринское шоссе'),(135,'От Белорусского вокзала электричкой до станции \"Звенигород\";\nОт станции метро \"Кунцевская\" автобусом № 452;\nОт станции метро \"Тушинская\" автобусом № 455;\nОт станции метро \"Строгино\" автобусом № 881.','На автомобиле добираться до Звенигорода можно либо по Новорижскому шоссе, либо по Можайскому.'),(136,'От станции метро \"Октябрьское поле\" и \"Сокол\" троллейбусом № 59 или маршрутным такси № 59м до остановки \"Универмаг\";\nОт станции метро \"Полежаевская\" автобусами № 155,155к, маршрутными такси № 190м,593м,597м или троллейбусами 20к,21,20,35к,35,86,65 до остановки \"Бульвар Генерала Карбышева\"','На автомобиле удобно добираться по проспекту Маршала Жукова, как из центра города, так и со стороны области.'),(137,'От Белорусского вокзала электричкой до станции \"Одинцово\", далее на автобусе № 46 или маршрутных такси № 46, 68 до остановки \"Поворот на Власиху\"','На автомобиле до с. Лайково удобно добираться по объездной платной трассе в обход Одинцова. Также можно добраться по Рублево-Успенскому шоссе или по Можайскому и Минскому через город Одинцово.'),(138,'До ЖК быстрее всего добираться от станции метро \"Речной вокзал\", дорога займет порядка 15 минут пешком через парк \"Дружбы\"','На автомобиле удобно добираться по Ленинградскому шоссе как из центра города, так и со стороны МКАД'),(139,'От станции метро \"Полежаевская\" маршрутное такчи № 386м или автобусы № 294, 48 до остановки \"Карамышевская набережная\"','На автомобиле удобно подъезжать со Звенигородского шоссе как из центра города, так и со стороны МКАД'),(140,'10 минут пешком от станции метро \"Бабушкинская\"','На автомобиле из центра города по Ярославскому шоссе, далее по Енисейской улице. Со стороны МКАД по Осташковской улице, далее по улице Широкая и Тайнинская.'),(141,'МФК «Лайнер» расположен в шаговой доступности от станции метро \"Динамо\". В будущем в 300 метрах от комплекса откроется станция метро \"Ходынское поле\"','Удобную транспортную доступность комплексу обеспечивает Ленинградский проспект и Третье транспортное кольцо'),(142,'ЖК «Лица» расположен в шаговой доступности от станции метро \"Динамо\". В будущем в 300 метрах от комплекса откроется станция метро \"Ходынское поле\"','Удобную транспортную доступность комплексу обеспечивает Ленинградский проспект и Третье транспортное кольцо'),(143,'От Белорусского вокзала на электричке до станции \"Дачное\" или \"Жаворонки\"','На автомобиле двигаться примерно 22 километра от МКАд по Можайскому шоссе, жилой комплекс будет с правой стороны.'),(144,'От станции метро \"Юго-Западная\" маршруты 227 и 227к- проехать 18 остановок до МКАД. Далее пересесть на автобус № 531 и ехать до дома отдыха «Красная Пахра». От него пройти пешком 1,2 км, до ул. Промышленная;\nОт станции метро \"Теплый Стан\" автобус № 531, ехать до дома отдыха «Красная Пахра». От него пройти пешком 1,2 км, до ул. Промышленная.','Ехать по Калужскому шоссе из центра около 20 км, проехав МакДоналдс, на следующем светофоре (направо пойдет ул. Физическая) ехать по прямой. Примерно через 2 км от светофора с правой стороны будетстроительный рынок. Нужно заехать на его территорию и продолжать движение вдоль Калужского шоссе. Доехав до развилки, повернуть направо, на ул. Промышленную. Еще около 400 метров вперед и по правую руку появится ЖК «Легенда».'),(145,'От железнодорожной станции \"Люберцы-1\" маршрутным такси №9 или автобусом №31до остановки \"Мотяково\"','На автомобиле со стороны МКАД лучше всего ехать через столичный район Некрасовка по Косинскому шоссе примерно 14 километров.'),(146,'От станции метро \"Медведково\" маршрутным такси № 502 до остановки \"РБК\";\nОт железнодорожной станции \"Мытищи\" автобусами № 22,23,26,31,314 или маршрутными такси № 22,26 до остановки \"ГАИ\"','Со стороны МКАД ехать сначала по Осташковскому шоссе, далее по Волковскому. Дорога от МКАД составит примерно 7 километров. Также можно добраться и по Яросласкому шоссе через Олимпийский проспект в Мытищах.'),(147,'От станции метро \"Медведково\" маршрутные такси № 169,177 до остановки \"Улица Колпакова\"\nОт железнодорожной станции \"Мытищи\" маршрутные такси № 177,8','Со стороны МКАД ехать сначала по Осташковскому шоссе, далее по Волковскому. Дорога от МКАД составит примерно 7 километров. Также можно добраться и по Яросласкому шоссе через Олимпийский проспект в Мытищах.'),(148,'От станций метро \"Щелковская\" или \"Партизанская\" автобусы и маршрутные такси № 338, 384, 889, 19 до остановки \"Химзавод\"','Со стороны МКАД  по Щелковскому шоссе двигаться примерно 6 километров. ЖК будет с левой стороны.'),(149,'От станций метро \"Щелковская\" или \"Партизанская\" автобусы и маршрутные такси № 338, 384, 889, 19 до остановки \"Химзавод\"','Со стороны МКАД  по Щелковскому шоссе двигаться примерно 6 километров. ЖК будет с левой стороны.'),(150,'От станции метро  \"Речной вокзал\" автобусами № 400, 851  до остановки \"Монумент / ИКЕА\";\nОт станции метро  \"Планерная\" автобусами № 817;\nОт железнодорожной станции Химки автобусом или маршрутным такси № 3 до остановки \"Улица 9 Мая, 18\"','На автомобиле со стороны МКАД примерно 4 километра по Ленинградскому шоссе. Также можно подъехать со стороны МКАД по Новокуркинскому шоссе или Молодежной улице через Химки.'),(151,'Электричкой от Ярославского вокзала (м. Комсомольская) до платформы \"Валентиновка\", далее пешком 15 минут;\nОт станции метро \"ВДНХ\" автобусом №392 до остановки «Проспект Космонавтов», далее пешком 1,5 км до ЖК «Валентиновка Парк».','На автомобиле можно добраться как по Ярославскому шоссе, так и по Щелковскому.'),(152,'ЖК ЖК «Ривер Парк» находится в шаговой доступности от станции метро \"Коломенска\", дорога пешком займет 15 минут.\nТакже можно доехать на автобусах № 670к, 670, 724, 751 или маршрутных такси № 670м, 724м от станции метро \"Коломенская\" до остановки \"Диспансер\"','Подъезжать удобно по проспекту Андропова, далее по Судостроительной улице до конца.'),(153,'От станции метро \"Алтуфьево\" автобусом или маршрутным такси № 459 до вокзала «Лобня», далее автобусом №1 или 21 до ост. «мкрн. Катюшки»;\nОт Савеловского вокзала в Москве до станции «Лобня», далее автобусом №1 или 21 до ост. «мкрн. Катюшки».\n','На автомобиле лучше добираться по Дмитровскому шоссе, также возможно проехать через Ленинградское.'),(154,'От станции метро «Юго-Западная» маршрутное такси № 510 до остановки «Марушкино»;\nОт Киевского вокзала электричкой до станции «Толстопальцево», далее автобусом № 32 или маршрутным такси № 510 до остановки «Марушкино»;\nОт Киевского вокзала электричкой до станции «Кокошкино», далее автобусjv № 510 до остановки  «Марушкино».','До жилого комплекса «Зеленая Москва-2» лучше всего добираться по Боровскому шоссе. В качестве альтернативы можно использовать Киевское шоссе. От МКАД ехать придется примерно 17 километров.'),(155,'От Казанского вокзала, станций метро \"Электрозаводская\" или \"Выхино\" электричкой до станции \"Фабричная\" или \"Раменки\", далее пешком до Десантной улице примерно 10-15 минут;\nОт станции метро \"Выхино\" маршрутным такси № 424','через г. Люберцы по Новорязанскому шоссе, по трасе А102 через Пехорку, Быково и город Жуковский;\nчерез г. Люберцы по той же Новорязанской трассе, но уже минуя Октябрьский, затем Островцы, Верею, и Жуковский.\nчерез г. Люберцы по Егорьевскому шоссе через п. Хрипань или Донино.'),(156,'От станций метро «Юго-Западная» маршрутом № 304 до остановки «Дачное товарищество «Отдых»;\nО станции метро «Теплый стан» маршрутами № 522 и № 526 до остановки «Дачное товарищество «Отдых»;\nОт станции Крекшино маршрутное такси №875М на Троицк.','На автомобиле можно добраться по Киевскому или Боровскому шоссе. От МКАД примерно 22 километра, далее на эстакаде поворот налево на Первомайское. Двигаться еще примерно 1 километр, ЖК будет по правую руку.'),(157,'От ж/д станции \"Расторгуево\" троллейбусы пешком 15 минут;\nОт станции метро \"Домодедовская\" автобусом или маршрутным такси № 364 до остановки \"ВНИИКОП\"\n',' Жилой комплекс расположен в 3-х минутах езда от МКАД по трассе М4 «Дон'),(158,'От станции метро «Молодежная» маршрутное такси № 597  до конечной остановки у железнодорожной станции «Ромашково». Далее пешком 10-15 минут;\nОт железнодорожной станции «Ромашково» Усовского ответвления Белорусского направления пешком 10-15 минут.','От развязки на 55-ом км МКАД в сторону Техцентра «Кунцево», через поселок Немчиновка по ул. Московская до пересечения с Советским проспектом. Далее есть 2 варианта проезда: либо направо по Советскому проспекту до указателя дер. Ромашково, около ж/д платформы поворот налево, далее прямо до жилого комплекса; либо налево по Советскому проспекту, через Немчиновку до перекрестка с проспектом Революции, далее направо по главной дороге не сворачивая до жилого комплекса.'),(159,'От станции метро \"Медведково\" автобусы № 314,314к до остановки \"Болтино\";\nОт железнодорожной станции \"Мытищи\" автобусы № 31, 314 до остановки \"Болтино\".','От МКАД (развязка на 90-м километре) примерно 9 километров по Осташковскому шоссе до деревни Болтино.'),(160,'От станции метро «Щелковская» автобус №349 или маршрутное такси №349т до автобусной станции «Щелково–7»;\nОт станции метро «ВДНХ» автобус №316 до остановки «Гор.Бани» или «Школа», далее пересесть на автобус №40 и ехать до остановки «Рынок»;\nОт Ярославского вокзала до ж/д станции «Воронок» или станции «Щелково», далее автобусом или маршрутным такси №7 до автобусной остановки «Щелково-7». ','По Щелковскому шоссе двигаться 13 км от Москвы до поворота налево на Фряновское шоссе, далее прямо и после моста поворот налево на Талсинскую улицу, после поворот направо на улицу Фрунзе (ориентир заправка Газпромнефть), на перекрестке поворот направо (ориентир автобусная остановка и указатель Щелково-7) на улицу Неделина, после шиномонтажа заезд в микрорайон Богородский.'),(161,'От Ярославского вокзала на электричке до станции \"Красноармейск\", далее пешком 15 минут;\nОт станции метро \"ВДНХ\" автобус № 317.',' На автомобиле двигаться примерно 17 километров от МКАД по Ярославскому шоссе, далее поворот на город Красноармейск. Проехать еще 17 километров и повернуть направо на улицу Новая Жизнь, илой комплекс будет с правой стороны.'),(162,'От станции метро \"Планерная\" троллейбусом №202 до остановки \"Нагорное шоссе\";\nОт станции метро \"Речной вокзал\" маршрутным такси №986 до остановки \"Юбилейный проспект\";\nОт железнодорожной станции \"Химки\" автобусами № 1,26,3,42,51 или маршрутными такси №19,3,326,469 до остановки \"Юбилейный проспект\".\n','ЖК «Альфа Центавра» находится в двух километрах от МКАД по Ленинградскому шоссе. Заезд на улицу 9 Мая через Юбилейный проспект.'),(163,'От Савеловского вокзала до станции \"Лобня\", далее автобусами № 2 или 21 до остановки \"Микрорайон Южный\"\n','На автомобиле можно добраться как по Дмитровскому шоссе, так и по Ленинградскому'),(164,'От станции метро \"Митино\" автобусом № 400К до остановки «Зелёный бор»;\nОт станции метро \"Тушинская\" автобусом №400Т до остановки «Зелёный бор»;\nОт станции метро \"Сходненская\" автобусом №460м до остановки «Зелёный бор»;\nОт железнодорожной станции \"Крюково\" автобусами № 366, 707м, 460м, 16, до остановок «Кутузовская Слобода» или Пионерский лагерь \"Спутник\".','По Пятницкому шоссе до поворота на Зеленоград. Далее примерно 1,5 км по Кутузовскому шоссе, ЖК будет с правой стороны.\nПо Ленинградскому шоссе до поворота на Зеленоград. Далее 5 км по Панфиловскому проспекту, пересечь по эстакаде железнодорожные пути, далее двигаться до поворота налево на улицу Каменка, затем первый перекресток налево на Георгиевский проспект. На круговом движении съехать на Кутузовское шоссе.'),(165,'От станции метро «Киевская» автобусами № 477 или 454 до остановки \"мкр. Кутузовский / Лохино\" или \"Трехгорка\";\nОт станции метро «Парк Победы» автобусами № 457, 442, 339 до остановки \"мкр. Кутузовский / Лохино\" или \"Трехгорка\";\nОт станции метро «Славянский бульвар» автобусом или маршрутным такси №139 до остановки \"мкр. Кутузовский / Лохино\" или \"Трехгорка\";\nОт железнодорожной станции «Трехгорка» Белорусского направления МЖД пешком 15 минут','По направлению от Москвы по Можайскому шоссе двигаться примерно 4 км, далее поворот направо на Одинцово, после эстакады съезд направо на «Трехгорку». На круговом развернуться в обратную сторону, не доезжая до Можайского шоссе повернуть направо на улицу Чистяковой.'),(166,'От станции метро «Домодедовская» автобусом или маршрутным такси №505 до остановки «Заборье».\nОт Павелецкого вокзала на электричкедо станции «Взлетная».\n','До ЖК «Город Счастья» на автомобиле можно добраться по трассе М4-Дон, Каширскому или Симферопольскому шоссе. От МКАД примерно 23 километра.'),(167,'От станции метро \"Бунинская аллея\" маршрутное такси № 1001 до остановки \"Никольское\";\nОт станции метро \"Аннино\" автобус № 864;\nОт станции метро \"Бульвар Дмитрия Донского\"автобусы № 864, 422, маршрутные такси № 422, 885, 548;\nОт Курского вокзала, от станции метро \"Площадь Ильича\", от станции метро \"Текстильщики\", от станции метро \"Царицино\" электричкой до железнодорожной станции \"Щербинка\", далее пешком 15 минут до ЖК «Прима-Парк»','На автомобиле из Москвы примерно 7 километров от МКАД по Варшавскому шоссе, далее поворот направо на Щербинку, проехать железнодорожный переезд и далее двигаться по Староникольской улице'),(168,'От станции метро «Щукинская» автобусом или маршрутным такси №480 до остановки Лешково, далее пересадка на автобус или маршрутное такси №23 (в сторону ж/д станции Нахабино), после КП «Павловы озера» не доезжая д. Лобаново, на месте строительства расширения дороги, въезд на территорию ЖК «Павловский квартал» (синий забор) - остановка «По требованию»;\nНа электричке от Курского (или Рижского) вокзала, а так же от ж/д станций Тушино (метро Тушинская), Дмитровская (метро Дмитровская), Ленинградская (метро Войковская) до ж/д станции Нахабино, затем на автобусе или маршрутном такси №23 (в сторону Павловской Слободы), не доезжая КП «Павловы озера», не доезжая д. Исаково, напротив строительства расширения дороги, въезд на территорию ЖК «Павловский квартал» (синий забор) - остановка «По требованию»','Двигаться по Новорижскому шоссе прмерно 17 километров, далее свернуть направо гна Лобаново. Либо по Волоколамскому шоссе через Нахабино также ехать до Лобаново.'),(169,'От станции метро \"Новокосино\" маршрутное такси № 487 до остановки \"1-я Лесная улица\";\nОт Курского вокзала на электричке до станции \"Электроугли\", далее маршрутное такси № 9 до остановки \"1-я Лесная улица\"','На автомобиле от МКАД ехать примерно 26 километров по Носовихинскому шоссе'),(170,'От Курского вокзала или станций метро \"Текстильщики\" и \"Царицыно\" электричкой до станции “Подольск”, а далее автобусом или маршрутным такси №1034 или автобусом № 1032 до остановки \"По требования\" (в поселке Поливаново)','На автомобиле можно добраться как по Калужскому шоссе (примерно 22 километра от МКАД), так и по Варшавскому (в этом случае длина пути составит уже порядка 30 километров)'),(171,'От Ленинградского вокзала в Москве или от железнодорожной станции “Петровско-Разумовское” электричкой до станции “Березки-Дачные” (время в пути составит в среднем от 45 до 60 минут), от станции 5-10 минут пешком.','На автомобиле удобнее добираться по Ленинградскому шоссе, от МКАД это примерно 36 километров.'),(172,'От станции метро \"Партизанская\" маршрутные такси № 272, 322м или троллейбусы № 22, 87. Или же пешком 10 минут;\nОт станции метро \"Семеновская\" маршрутные такси № 69м, 452м, 322м, 752м, 311м или автобус № 783 или трамваи  № 32, 11, 34. Или же пешком 15 минут.','На автомобиле подъезжать удобно с Щербаковской улицы. Их крупных улиц рядом неподалеку расположены Большая Черкизовская, шоссе Энтузиастов и Измайловское шоссе.'),(173,'От станции метро \"Медведково\" автобусом № 314к до остановки \"Микрорайон Пирогово\";\nОт железнодорожной станции \"Мытищи\" автобусами 23, 26 или маршрутным такси № 26 до остановки \"Округ Пирогово\".\n','На автомобиле двигаться примерно 10 километров от МКАД по Осташковскому шоссе до Пирогово.'),(174,'От станции метро \"Бульвар Дмитрия Донского\" маршрутными такси № 729м, 760м, 761м. до остановки \"Химзавод\", далее автобусом или маршрутным такси № 379 до остановки \"Правление\";\nОт станции метро \"Домодедовская\" автобусом или маршрутным такси № 364 до остановки «Станция Расторгуево», далее  автобусом или маршрутным такси  № 379 до остановки \"Правление\";\nОт Павелецкого вокзала электричкой до железнодорожной станции \"Расторгуево\",  далее  автобусом или маршрутным такси  № 379 до остановки \"Правление\";\nОт Курского вокзала электричкой до железнодорожной станции \"Бутово\", далее  автобусом или маршрутным такси  № 379 до остановки \"Правление\".','На автомобиле со стороны Москвы удобнее добираться по Симферопольскому шоссе. Также можно воспользоваться Варшавским шоссе или трассой М-4 «Дон», далее по Расторгуевскому шоссе. От МКАД ЖК находится примерно в 5-7 километрах езды.'),(175,'От станции метро \"Партизанская\" автобусы или маршрутные такси № 322, 382, 394 до остановки \"Вокзал\" (в Ногинске). Далее можно дойти пешком за 10-15 минут, лиюо проехать 2 остановки на маршрутном такси №1;\nОт Курского вокзала на электричке до станции \"Ногинск\", далее можно дойти пешком за 10-15 минут, лиюо проехать 2 остановки на маршрутном такси №1.','От МКАД на автомобиле проехать 37 километров по Горьковскому шоссе, далее свернуть на Электростальское шоссе и через 2 километра повернуть направо на Бетонную улицу, далее двигаться прямо- улица перейдет в Аэроклубную улицу- жилой комплекс \"Полет\" будт с правой стороны.'),(176,'От станции метро \"Преображенская площадь\" 5-10 минут пешком','На автомобиле подъезжать можно как с Большой Черкизовской улицы, так и с Краснобогатырской улицы'),(177,'От станции метро \"Юго-Западная\" маршрутное такси № 629м до остановки \"Мосгорломбард\"\n','На автомобиле удобно подъезжать с Мичуринского проспекта'),(178,'От станции метро \"Домодедовская\" автобусом № 364 до остановки \"Детский сад\";\nОт станции метро \"Кантемировская\" автобусом или маршрутным такси № 489 до остановки \"Детский сад\";\nОт железнодорожной станции \"Расторгуево\" автобусами № 3,364,44,471,489,6,7,9 или маршрутными такси № 364,471,489 до остановки \"ВНИИКОП\"','На автомобиле до города Видное добираться удобно по трассе М-4 \"Дон\", дорога от МКАД составит всего около 3 километров'),(179,'От станции метро \"Молодежная\" на маршрутном такси № 121 до поселка Горки-10;\nОт железнодорожной станции \"Одинцово\" на автобусах № 55, 56, 32 до поселка Горки-10;\nОт железнодорожной станции \"Жаворонки\" на автобусе № 1025 до поселка Горки-10.','На автомобиле удобно добираться по Рублево-Успенскому шоссе, от МКАД дорога займет примерно 24 километра.'),(180,'От железнодорожной станции \"Химки\" автобусом № 14 или маршрутным такси № 16 до остановки \"Коммунальный проезд, 12\";\nОт станции метро \"Речной вокзал\" автобусом или маршрутным такси № 345 до конечной остановки (станция \"Химки\"), далее автобусом № 14 или маршрутным такси № 16 до остановки \"Коммунальный проезд, 12\";\nОт станции метро \"Планерная\" маршрутным такси № 469 до конечной остановки (станция \"Химки\"), далее автобусом № 14 или маршрутным такси № 16 до остановки \"Коммунальный проезд, 12\".','Двигаться от МКАД по Ленинградскому шоссе, далее направо на улице Репина, после чего налево на Коммунальный проезд. Примерно через километр пути покажется строительная площадка ЖК «Две столицы».'),(181,'ЖК «Дом на Люсиновской» находится в шаговой доступности от станций метро \"Добрынинская\", \"Серпуховская\", \"Октябрьская\" и \"Шаболовская\"','На автомобиле удобно подъезжать с Мытной или Люсиновской улиц'),(182,'Жилой комплекс \"Дом на Самаринской\" находится в пешей доступности от станций метро \"Тульская\" и \"Шаболовская\"','На автомобили удобно подъезжать с улиц Серпуховский Вал и Мытная. От ЖК можно быстро выехать на Садовое колцо и Третье Транспортное кольцо.'),(183,'От станции метро \"Бабушкинска\" автобусом №605 или маршрутным такси № 605м до остановки \"Изумрудная улица\";\nОт станции метро \"Медведково\" автобусами №774, 774а или маршрутным такси № 79м до остановки \"Напрудная улица\";\nОт железнодорожной станции \"Лось\" автобусами №176, 605 или маршрутным такси № 605м до остановки \"Напрудная улица\";\nОт железнодорожной станции \"Лосиноостровская\" автобусами №176, 185 или маршрутным такси № 185м до остановки \"Изумрудная улица\".','На автомобиле до района удобно добираться по Ярославскому шоссе, далее по Енисейской улице. Также в район удобно заезжать со стороны МКАД по развязке с Осташковским шоссе.'),(184,'Жилой квартал «1147» находится в шаговой доступности от станций метро \"Алексеевская\" и \"ВДНХ\"','На автомобиле удобно добираться по проспекту Мира'),(185,'ЖК «Волга» находится в шаговой доступности от станций метро “Комсомольская”, “Сухаревская” и “Красные ворота”','На автомобиле удобнее всего подъезжать с Садового кольца'),(186,'В шаговой доступности от ЖК «Карамель» находится станция метро \"Савеловская\"','На автомобиле можно добраться по Третьему транспортному кольцу. Из центра города по улице Новослободская, со стороны МКАД по Дмитровскому шоссе.'),(187,'От станции метро \"Митино\" маршрутным такси № 66 до остановки \"Химчистка\";\nОт железнодорожной станции \"Павшино\" автобусами или маршрутными такси № 806, 833 до остановки \"Химчистка\".','Со стороны МКАД на автомобиле двигаться примерно 4 километра по Волоколамскому шоссе, далее поворот направо на улицу Речная и двигаться прямо еще примерно 3 километра'),(188,'От Курского вокзала или от станций метро “Текстильщики” или “Царицыно” электричкой до железнодороной станции “Барыбино”, далее 10 минут пешком','От МКАД по трассе М4 “Дон” двигаться прямо примерно 30 километров до поворота на  Бронницы, далее  по трассе А-107 до поворота на деревню Образцово около 6 километров, далее движение прямо по главной дороге до поворота на деревню Гальчино, далее прямо еще примерно километр'),(189,'ЖК «Barkli Residence» находится в шаговой доступности от станций метро «Шаболовская», “Тульская” и “Ленинский проспект\"','Комплекс имеет хорошую транспортную доступность к Садовому и Третьему транспортному кольцу, а также к Ленинскому проспекту'),(190,'блаблабла','блаблабла');
